import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import Header from '../components/Header';
import Footer from '../components/Footer';

const ProfilePage = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState('profile');
  const [editMode, setEditMode] = useState(false);

  // 편집 가능한 사용자 정보
  const [editableUser, setEditableUser] = useState({
    name: '',
    email: '',
    birthYear: '',
    birthMonth: '',
    birthDay: '',
    birthTime: '',
    gender: '',
    phoneNumber: ''
  });

  useEffect(() => {
    // 실제 서비스에서는 API 호출로 사용자 정보를 가져옴
    // 여기서는 localStorage와 가상 데이터 사용
    const isLoggedIn = localStorage.getItem('isLoggedIn');

    if (!isLoggedIn) {
      navigate('/login');
      return;
    }

    // 가상의 사용자 데이터
    const mockUser = {
      id: '1',
      name: '김운세',
      email: 'user@example.com',
      birthYear: '1990',
      birthMonth: '3',
      birthDay: '15',
      birthTime: '09:30',
      gender: '남성',
      phoneNumber: '010-1234-5678',
      membership: '일반',
      registeredAt: '2023-02-15',
      analysisHistory: [
        { id: '1', type: '기본 운세', date: '2023-03-20', title: '2023년 운세 분석' },
        { id: '2', type: '사업운', date: '2023-04-05', title: '사업 운세 분석' },
        { id: '3', type: '연애운', date: '2023-05-12', title: '연애 운세 분석' }
      ],
      purchaseHistory: [
        { id: '1', product: '프리미엄 운세 분석', price: '39,000원', date: '2023-04-01', status: '결제완료' },
        { id: '2', product: '2023년 종합 운세', price: '29,000원', date: '2023-01-05', status: '결제완료' }
      ]
    };

    setTimeout(() => {
      setUser(mockUser);
      setEditableUser({
        name: mockUser.name,
        email: mockUser.email,
        birthYear: mockUser.birthYear,
        birthMonth: mockUser.birthMonth,
        birthDay: mockUser.birthDay,
        birthTime: mockUser.birthTime,
        gender: mockUser.gender,
        phoneNumber: mockUser.phoneNumber
      });
      setLoading(false);
    }, 1000);
  }, [navigate]);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const handleEditToggle = () => {
    if (editMode) {
      // 실제로는 여기서 API 호출로 사용자 정보 업데이트
      setUser({
        ...user,
        ...editableUser
      });
    }
    setEditMode(!editMode);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditableUser({
      ...editableUser,
      [name]: value
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('userEmail');
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="profile-page">
        <Header />
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>로딩 중...</p>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="profile-page">
      <Header />

      <main>
        <section className="profile-header">
          <div className="container">
            <motion.h1
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              마이페이지
            </motion.h1>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              {user.name}님의 개인정보와 이용 내역을 확인하세요
            </motion.p>
          </div>
        </section>

        <section className="profile-content">
          <div className="container">
            <div className="profile-tabs">
              <button 
